export const getPlatformLogo = (platform: string) => {
  switch (platform.toLowerCase()) {
    case 'spotify':
      return SpotifyLogo;
    case 'apple music':
      return AppleMusicLogo;
    case 'youtube music':
      return YouTubeMusicLogo;
    case 'amazon music':
      return AmazonMusicLogo;
    case 'deezer':
      return DeezerLogo;
    case 'tiktok':
      return TikTokLogo;
    case 'soundcloud':
      return SoundCloudLogo;
    case 'pandora':
      return PandoraLogo;
    case 'bandcamp':
      return BandcampLogo;
    default:
      return SpotifyLogo;
  }
};

